mapper.select:
SELECT *
FROM (SELECT ROWNUM R_NUM, B.*
        FROM (SELECT *
              FROM BOARD
              ORDER BY GROUPS DESC, LEV ASC
             ) B
     )    
WHERE R_NUM BETWEEN #{begin} AND #{end}

mapper.update:
UPDATE BOARD
SET LEV = LEV + 1
WHERE GROUPS = #{groups} AND LEV >= #{lev}
