mapper.select:

SELECT *
FROM (SELECT ROWNUM R_NUM, M.*
	        FROM (SELECT A.MOIM_SQ, A.MOIM_CD, A.MOIM_CA, A.MOIM_TITLE, A.MOIM_INTRO, A.MOIM_TAG, A.MOIM_ADDRESS, A.MOIM_CNT, A.MOIM_HIT, A.MOIM_DATE, 
	        		A.MOIM_DISPLAY_FL, B.BF_FILE AS MOIM_FILE
					FROM T_MOIM A, T_BOARD_FILE B WHERE A.MOIM_SQ=B.BD_SNO ORDER BY A.MOIM_SQ DESC
	             )M
	     )    



	WHERE R_NUM BETWEEN #{begin} AND #{end} and (MOIM_TITLE LIKE '%'|| #{stx2} ||'%' OR MOIM_ADDRESS LIKE '%'|| #{stx2} ||'%' OR MOIM_INTRO LIKE '%'|| #{stx2} ||'%' OR MOIM_TAG LIKE '%'|| #{stx2} ||'%' )


	WHERE R_NUM BETWEEN #{begin} AND #{end}


SELECT *
FROM (SELECT ROWNUM R_NUM, M.*
	        FROM (SELECT A.MOIM_SQ, A.MOIM_CD, A.MOIM_CA, A.MOIM_TITLE, A.MOIM_CNT, A.MOIM_HIT, A.MOIM_DATE, A.MOIM_DISPLAY_FL,
					B.BF_FILE AS MOIM_FILE
					FROM T_MOIM A, T_BOARD_FILE B WHERE A.MOIM_SQ=B.BD_SNO ORDER BY A.MOIM_SQ DESC
	             )M
	     )    

WHERE R_NUM BETWEEN #{begin} AND #{end} and MOIM_CA = #{cate2}

SELECT *
	FROM EMPLOYEES


			WHERE MOIM_CD = #{keyword}


		WHERE MOIM_AREA_SIDO = #{keyword}


		WHERE MOIM_AREA_GUGUN = #{keyword}


			WHERE MOIM_TITLE = #{keyword}


			WHERE MOIM_TAG = #{keyword}


			WHERE CREATE_DT= #{keyword}
